workspace.query:
xquery version "1.0-ml";
(: This script applies the envelope pattern to customer entitities.
 : We first test our envelope in Query Console.
 : The root element is named for the type of entity we are enveloping.
:)

let $docs := fn:collection("customer")
let $enveloped-docs :=
   for $doc in $docs return
   let $collections:= xdmp:document-get-collections($doc)
   return
     <customer>{
      <source>{$doc/customer/*}</source>
   }</customer>
return $enveloped-docs

xquery version "1.0-ml";

(: This script applies the envelope pattern to customer entitities.
 : Here we use cts:uris() to return the URIs we want to envelope.
:)

let $uris := cts:uris('','document', cts:collection-query("customer"))

let $enveloped-docs :=
   for $uri in $uris 
   let $source := fn:doc($uri)
   return
     <customer>{
      <source>{$source/customer/*}</source>
   }</customer>

return $enveloped-docs

xquery version "1.0-ml";
(: This script applies the envelope pattern to customer entitities
 : and inserts the results in the database.
 : Enveloped documents are added to the 'enveloped' collection.
:)

let $collection := "customer"
let $uris := cts:uris('','document', cts:collection-query($collection))

let $enveloped-docs :=
   for $uri in $uris 
   let $collections := xdmp:document-get-collections($uri)
   let $source := fn:doc($uri)
   let $doc := 
                <customer>{
                  <source>{$source/customer/*}</source>
                }</customer>
   return xdmp:document-insert($uri, $doc, (), ($collections, "enveloped"))
return ($enveloped-docs,
        fn:concat("You have just enveloped the ",$collection," collection entities.")
       )

xquery version "1.0-ml"; (: This returns the URIs for the documents returned by a more complex query. :)
let $uris := cts:uris('','document', cts:and-query((cts:collection-query("customer"), cts:collection-query("system-a"), cts:collection-query("enveloped"))))
return $uris

xquery version "1.0-ml";

(: This script creates the canonical section for
 : our enveloped documents from system-a and 
 : inserts them before the <source> sections 
 : within our envelopes.
:)
let $uris := cts:uris('','document', cts:and-query((cts:collection-query("customer"), 
                                                   cts:collection-query("system-a"),
                                                   cts:collection-query("enveloped"))))
for $uri in $uris
let $doc := fn:doc($uri)
let $zip := $doc/customer/source/shipping_address/zip/text()
let $header := <canonical><shipping-zip>{$zip}</shipping-zip></canonical>
return xdmp:node-insert-before($doc/customer/source, $header);

"You just added <canonical> sections to your enveloped documents from system-a";

xquery version "1.0-ml";
(: This script creates the canonical section for
 : our enveloped documents from system-b and 
 : inserts them before the <source> sections 
 : within our envelopes.
:)

let $uris := cts:uris('','document', cts:and-query((cts:collection-query("customer"), 
                                                   cts:collection-query("system-b"),
                                                   cts:collection-query("enveloped"))))

for $uri in $uris
let $doc := fn:doc($uri)
let $zip := $doc/customer/source/postal/text()
let $header := <canonical><shipping-zip>{$zip}</shipping-zip></canonical>
return xdmp:node-insert-before($doc/customer/source, $header);

"You just added <canonical> sections to your enveloped documents from system-b";

xquery version "1.0-ml";

(: This script mocks up the header section we'll add
 : to our enveloped documents to start storing a 
 : canonical model of the data within our envelopes.
:)

let $uris := cts:uris('','document', cts:and-query((cts:collection-query("customer"), 
                                                   cts:collection-query("system-a"),
                                                   cts:collection-query("enveloped"))))

for $uri in $uris
let $doc := fn:doc($uri)
let $zip := $doc/customer/source/shipping_address/zip/text()
let $header := <canonical><shipping-zip>{$zip}</shipping-zip></canonical>
return ($header) (: , $doc) :)

xquery version "1.0-ml";
(: This script adds an additional metadata section : to our enveloped documents from system-a and : inserts them before the <canonical> section : within our envelopes. :)
let $uris := cts:uris('','document', cts:and-query((cts:collection-query("customer"), cts:collection-query("system-a"), cts:collection-query("enveloped"))))
for $uri in $uris let $doc := fn:doc($uri) let $metadata := <metadata> <source>POS</source> <date>{fn:current-date()}</date> <lineage>v01 transform</lineage> </metadata> return xdmp:node-insert-before($doc/customer/canonical, $metadata);
"You just added <metadata> sections to your enveloped documents from system-a";

xquery version "1.0-ml"; (: This script adds an additional metadata section : to our enveloped documents from system-b and : inserts them before the <canonical> section : within our envelopes. :)
let $uris := cts:uris('','document', cts:and-query((cts:collection-query("customer"), cts:collection-query("system-b"), cts:collection-query("enveloped"))))
for $uri in $uris let $doc := fn:doc($uri) let $metadata := <metadata> <source>Finance</source> <date>{fn:current-date()}</date> <lineage>v01 transform</lineage> </metadata> return xdmp:node-insert-before($doc/customer/canonical, $metadata); "You just added <metadata> sections to your enveloped documents from system-b";
