xquery version "1.0-ml";
(: : Insert the Template. : The function tde:template-insert() will validate the template; insert it into the Schemas database; : and assign the special collection "http://marklogic.com/xdmp/tde" :)
import module "http://marklogic.com/xdmp/tde" at "/MarkLogic/tde.xqy";
let $my-first-TDE:= <template xmlns="http://marklogic.com/xdmp/tde"> <context>/customer</context> <rows> <row> <schema-name>customer</schema-name> <view-name>customers</view-name> <columns> <column> <name>customer_id</name> <scalar-type>long</scalar-type> <val>source/id</val> <invalid-values>ignore</invalid-values> </column> <column> <name>last_name</name> <scalar-type>string</scalar-type> <val>source/lname</val> <invalid-values>ignore</invalid-values> </column> <column> <name>city</name> <scalar-type>string</scalar-type> <val>source/city</val> <invalid-values>ignore</invalid-values> </column> <column> <name>state</name> <scalar-type>string</scalar-type> <val>source/state</val> <invalid-values>ignore</invalid-values> </column> <column> <name>shipping_zip</name> <scalar-type>string</scalar-type> <val>canonical/shipping-zip</val> <invalid-values>ignore</invalid-values> </column> </columns> </row> </rows> </template>
return
( tde:template-insert( "/test/my-first-TDE.xml" , $my-first-TDE , xdmp:default-permissions() , "TDE-EA" ), "You just saved your TDE template template the Schemas database.", "Change your database to Schemas, and click 'Explore' to view the template URI." )

