xquery version "1.0-ml"; (: # simple SQL query from the Optic API XQuery :)
import module namespace op="http://marklogic.com/optic" at "/MarkLogic/optic.xqy";
op:from-view("customer", "customers") => op:select(("customer_id", "last_name", "shipping_zip")) => op:order-by("shipping_zip") => op:result()

'use strict';

// simple SQL query from the Optic API JavaScript

const op = require('/MarkLogic/optic');

op.fromView('customer', 'customers')
   .select(['customer_id', 'last_name', 'shipping_zip'])
   .orderBy('shipping_zip')
   .result();
