workspace.query:
xquery version "1.0-ml";

import module namespace op = "http://marklogic.com/optic" at "/MarkLogic/optic.xqy";

op:from-triples(
   op:pattern( op:col("s"), op:col("p"), op:col("o"))
   )
=>op:limit(10)
=>op:result()

xquery version "1.0-ml";
declare namespace html = "http://www.w3.org/1999/xhtml";

cts:triples()[1 to 10]

PREFIX : <http://marklogic.com/example/ontology/>  
PREFIX ids: <http://marklogic.com/example/products/> 
PREFIX taxo: <http://marklogic.com/example/taxonomies/> 
PREFIX xs: <http:///www.w3.org/2001/> 

SELECT 
 ?name ?sku
WHERE
{
  ?s a :Product ;
     :name ?name ;
     :sku ?sku .
}
LIMIT 10

sem:sparql('PREFIX : <http://marklogic.com/example/ontology/>  
PREFIX ids: <http://marklogic.com/example/products/> 
PREFIX taxo: <http://marklogic.com/example/taxonomies/> 
PREFIX xs: <http:///www.w3.org/2001/> 

SELECT 
 ?name ?sku
WHERE
{
  ?s a :Product ;
     :name ?name ;
     :sku ?sku .
}
LIMIT 10')

xquery version "1.0-ml";

import module namespace op = "http://marklogic.com/optic" at "/MarkLogic/optic.xqy";

let $o := op:prefixer("http://marklogic.com/example/ontology/") 
let $rdf := op:prefixer("http://www.w3.org/1999/02/22-rdf-syntax-ns#")

return
op:from-triples( (
   op:pattern( op:col("p"), $rdf("type"), $o("Product") ),
   op:pattern( op:col("p"), $o("name"), op:col("name") ),
   op:pattern( op:col("p"), $o("sku"),  op:col("sku"))
   ))
=>op:select(( "name", "sku" ) )
=>op:limit(10)
=>op:result()

xquery version "1.0-ml";
